    (window as any).openAuthModal = (tab: 'login' | 'register' = 'login') => {
      setInitialTab(tab);
      setIsOpen(true);
    };

    return () => {
      delete (window as any).openAuthModal;
    };
  }, []);

  return (
    <AuthModal
      isOpen={isOpen}
      onClose={() => setIsOpen(false)}
      initialTab={initialTab}
    />
  );
}

