      role: 'user',
      points: 750,
      level: 5,
      createdAt: '2024-02-20'
    }
  ];

  private challenges: Challenge[] = [
    {
      id: 1,
      title: 'SQL Injection –≤ —Ñ–æ—Ä–º–µ –≤—Ö–æ–¥–∞',
      description: '–ù–∞–π–¥–∏—Ç–µ —Å–ø–æ—Å–æ–± –æ–±–æ–π—Ç–∏ –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏—é –∏—Å–ø–æ–ª—å–∑—É—è SQL injection',
      category: 'web',
      difficulty: 'beginner',
      points: 100,
      flag: 'cyberwhale{sql_bypass_admin}',
      hints: ['–ü–æ–ø—Ä–æ–±—É–π—Ç–µ –≤–≤–µ—Å—Ç–∏ \' OR 1=1--', '–û–±—Ä–∞—Ç–∏—Ç–µ –≤–Ω–∏–º–∞–Ω–∏–µ –Ω–∞ –ø–æ–ª–µ –ø–∞—Ä–æ–ª—è'],
      isActive: true,
      createdAt: '2024-01-15',
      updatedAt: '2024-01-15'
    }
  ];

