import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useToast } from '@/hooks/use-toast';
import { 
  Shield, 
  Plus, 
  Edit, 
  Trash2,
  Save,
  RefreshCw,
  BookOpen,
  Upload,
  Download,
  X,
  CheckCircle,
  AlertTriangle
} from 'lucide-react';

interface Challenge {
  id: number;
  title: string;
  description: string;
  category: string;
  difficulty: 'beginner' | 'intermediate' | 'advanced' | 'expert';
  points: number;
  flag: string;
  hints: string[];
  fileUrl?: string;
  isActive: boolean;
  createdAt: string;
  updatedAt: string;
}

const categories = [
  { value: 'web', label: 'Web Security' },
  { value: 'crypto', label: '–ö—Ä–∏–ø—Ç–æ–≥—Ä–∞—Ñ–∏—è' },
  { value: 'osint', label: 'OSINT' },
  { value: 'steganography', label: '–°—Ç–µ–≥–∞–Ω–æ–≥—Ä–∞—Ñ–∏—è' },
  { value: 'reverse-engineering', label: '–†–µ–≤–µ—Ä—Å-–∏–Ω–∂–∏–Ω–∏—Ä–∏–Ω–≥' },
  { value: 'forensics', label: '–§–æ—Ä–µ–Ω–∑–∏–∫–∞' },
  { value: 'pwn', label: 'PWN' },
  { value: 'programming', label: '–ü—Ä–æ–≥—Ä–∞–º–º–∏—Ä–æ–≤–∞–Ω–∏–µ' },
  { value: 'network', label: '–°–µ—Ç–µ–≤–∞—è –±–µ–∑–æ–ø–∞—Å–Ω–æ—Å—Ç—å' }
];

const difficulties = [
  { value: 'beginner', label: '–ù–∞—á–∏–Ω–∞—é—â–∏–π', color: 'bg-green-500' },
  { value: 'intermediate', label: '–°—Ä–µ–¥–Ω–∏–π', color: 'bg-yellow-500' },
  { value: 'advanced', label: '–ü—Ä–æ–¥–≤–∏–Ω—É—Ç—ã–π', color: 'bg-orange-500' },
  { value: 'expert', label: '–≠–∫—Å–ø–µ—Ä—Ç', color: 'bg-red-500' }
];

export default function FinalAdminPanel() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [password, setPassword] = useState('');
  const [challenges, setChallenges] = useState<Challenge[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [editingChallenge, setEditingChallenge] = useState<Challenge | null>(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [activeTab, setActiveTab] = useState('challenges');
  const { toast } = useToast();

  const [challengeForm, setChallengeForm] = useState({
    title: '',
    description: '',
    category: 'web',
    difficulty: 'beginner' as Challenge['difficulty'],
    points: 100,
    flag: '',
    hints: [''],
    fileUrl: ''
  });

  const handlePasswordSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch('/api/admin/verify-password', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ password })
      });

      const data = await response.json();

      if (data.success) {
        setIsAuthenticated(true);
        setPassword('');
        await loadChallenges();
        toast({
          title: "–î–æ—Å—Ç—É–ø —Ä–∞–∑—Ä–µ—à–µ–Ω",
          description: "–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ –∞–¥–º–∏–Ω –ø–∞–Ω–µ–ª—å"
        });
      } else {
        toast({
          title: "–û—à–∏–±–∫–∞ –¥–æ—Å—Ç—É–ø–∞",
          description: data.message,
          variant: "destructive"
        });
      }
    } catch (error) {
      toast({
        title: "–û—à–∏–±–∫–∞",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å –ø—Ä–æ–≤–µ—Ä–∏—Ç—å –ø–∞—Ä–æ–ª—å",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const loadChallenges = async () => {
    try {
      const response = await fetch('/api/challenges');
      if (response.ok) {
        const data = await response.json();
        setChallenges(data);
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∑–∞–¥–∞–Ω–∏–π:', error);
    }
  };

  const handleCreateChallenge = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch('/api/admin/challenges', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          ...challengeForm,
          hints: challengeForm.hints.filter(hint => hint.trim() !== '')
        })
      });

      const data = await response.json();

      if (response.ok) {
        toast({
          title: "–ó–∞–¥–∞–Ω–∏–µ —Å–æ–∑–¥–∞–Ω–æ",
          description: "CTF –∑–∞–¥–∞–Ω–∏–µ —É—Å–ø–µ—à–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω–æ –≤ –±–∞–∑—É"
        });

        setShowCreateForm(false);
        resetForm();
        await loadChallenges();
      } else {
        throw new Error(data.message);
      }
    } catch (error: any) {
      toast({
        title: "–û—à–∏–±–∫–∞ —Å–æ–∑–¥–∞–Ω–∏—è",
        description: error.message,
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleUpdateChallenge = async (id: number) => {
    setIsLoading(true);

    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`/api/admin/challenges/${id}`, {
        method: 'PUT',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          ...challengeForm,
          hints: challengeForm.hints.filter(hint => hint.trim() !== '')
        })
      });

      const data = await response.json();

      if (response.ok) {
        toast({
          title: "–ó–∞–¥–∞–Ω–∏–µ –æ–±–Ω–æ–≤–ª–µ–Ω–æ",
          description: "–ò–∑–º–µ–Ω–µ–Ω–∏—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω—ã —É—Å–ø–µ—à–Ω–æ"
        });

        setEditingChallenge(null);
        resetForm();
        await loadChallenges();
      } else {
        throw new Error(data.message);
      }
    } catch (error: any) {
      toast({
        title: "–û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è",
        description: error.message,
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteChallenge = async (id: number) => {
    if (!confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç–æ –∑–∞–¥–∞–Ω–∏–µ?')) return;

    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`/api/admin/challenges/${id}`, {
        method: 'DELETE',
        headers: { 
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await response.json();

      if (response.ok) {
        toast({
          title: "–ó–∞–¥–∞–Ω–∏–µ —É–¥–∞–ª–µ–Ω–æ",
          description: "CTF –∑–∞–¥–∞–Ω–∏–µ —É–±—Ä–∞–Ω–æ –∏–∑ –±–∞–∑—ã"
        });
        await loadChallenges();
      } else {
        throw new Error(data.message);
      }
    } catch (error: any) {
      toast({
        title: "–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è",
        description: error.message,
        variant: "destructive"
      });
    }
  };

  const resetForm = () => {
    setChallengeForm({
      title: '',
      description: '',
      category: 'web',
      difficulty: 'beginner',
      points: 100,
      flag: '',
      hints: [''],
      fileUrl: ''
    });
  };

  const startEditing = (challenge: Challenge) => {
    setEditingChallenge(challenge);
    setChallengeForm({
      title: challenge.title,
      description: challenge.description,
      category: challenge.category,
      difficulty: challenge.difficulty,
      points: challenge.points,
      flag: challenge.flag,
      hints: challenge.hints.length > 0 ? challenge.hints : [''],
      fileUrl: challenge.fileUrl || ''
    });
  };

  const addHint = () => {
    setChallengeForm({
      ...challengeForm,
      hints: [...challengeForm.hints, '']
    });
  };

  const removeHint = (index: number) => {
    setChallengeForm({
      ...challengeForm,
      hints: challengeForm.hints.filter((_, i) => i !== index)
    });
  };

  const updateHint = (index: number, value: string) => {
    const newHints = [...challengeForm.hints];
    newHints[index] = value;
    setChallengeForm({
      ...challengeForm,
