import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '@/lib/auth/newProvider';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { AlertCircle, Loader2 } from 'lucide-react';
import { Link } from 'react-router-dom';

export function RegisterForm() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const { register, isLoading, error } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      return; // Password mismatch is handled by form validation
    }

    try {
      await register(username, email, password);
      navigate('/');
    } catch (err) {
      // Error is handled by the auth context
      console.error('Registration error:', err);
    }
  };

  return (
    <>
      {error && (
        <Alert variant="destructive" className="mb-6">
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <form className="space-y-6" onSubmit={handleSubmit}>
        <div>
          <Label htmlFor="username" className="block text-sm font-medium text-gray-200">
            –ò–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
          </Label>
          <div className="mt-1">
            <Input
              id="username"
              name="username"
              type="text"
              required
              className="bg-cyberdark-700 border-cyberdark-600"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="username"
              minLength={3}
              maxLength={30}
            />
          </div>
        </div>

        <div>
          <Label htmlFor="email" className="block text-sm font-medium text-gray-200">
            Email
          </Label>
          <div className="mt-1">
            <Input
              id="email"
              name="email"
              type="email"
              autoComplete="email"
              required
              className="bg-cyberdark-700 border-cyberdark-600"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="your@email.com"
            />
          </div>
        </div>

        <div>
          <Label htmlFor="password" className="block text-sm font-medium text-gray-200">
            –ü–∞—Ä–æ–ª—å
          </Label>
          <div className="mt-1">
            <Input
              id="password"
              name="password"
              type="password"
              required
              className="bg-cyberdark-700 border-cyberdark-600"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              minLength={6}
            />
          </div>
          <p className="mt-1 text-xs text-gray-400">–ú–∏–Ω–∏–º—É–º 6 —Å–∏–º–≤–æ–ª–æ–≤</p>
        </div>

        <div>
          <Label htmlFor="confirm-password" className="block text-sm font-medium text-gray-200">
            –ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç–µ –ø–∞—Ä–æ–ª—å
          </Label>
          <div className="mt-1">
            <Input
              id="confirm-password"
              name="confirm-password"
              type="password"
              required
              className={`bg-cyberdark-700 border-cyberdark-600 ${
                confirmPassword && password !== confirmPassword ? 'border-red-500' : ''
              }`}
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
            />
          </div>
          {confirmPassword && password !== confirmPassword && (
            <p className="mt-1 text-xs text-red-500">–ü–∞—Ä–æ–ª–∏ –Ω–µ —Å–æ–≤–ø–∞–¥–∞—é—Ç</p>
          )}
        </div>

        <div className="flex items-center">
          <input
            id="terms"
            name="terms"
            type="checkbox"
            required
            className="h-4 w-4 rounded border-cyberdark-600 bg-cyberdark-700 text-cyberblue-500 focus:ring-cyberblue-500"
          />
          <Label htmlFor="terms" className="ml-2 block text-sm text-gray-300">
            –Ø —Å–æ–≥–ª–∞—Å–µ–Ω —Å{' '}
            <Link to="/terms" className="font-medium text-cyberblue-500 hover:text-cyberblue-400">
              —É—Å–ª–æ–≤–∏—è–º–∏ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è
            </Link>{' '}
            –∏{' '}
            <Link to="/privacy" className="font-medium text-cyberblue-500 hover:text-cyberblue-400">
              –ø–æ–ª–∏—Ç–∏–∫–æ–π –∫–æ–Ω—Ñ–∏–¥–µ–Ω—Ü–∏–∞–ª—å–Ω–æ—Å—Ç–∏
            </Link>
          </Label>
        </div>

        <div>
          <Button
            type="submit"
            disabled={isLoading || (!!confirmPassword && password !== confirmPassword)}
            className="w-full bg-cyberblue-500 hover:bg-cyberblue-600"
          >
            {isLoading ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è...
              </>
            ) : (
              '–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è'
            )}
          </Button>
        </div>
      </form>
    </>
  );
}

