import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { mockChallenges, Challenge } from '@/data/challenges';
import { challengeHints, challengeFlags } from '@/data/challengeData';

// –ò–Ω—Ç–µ—Ä—Ñ–µ–π—Å –¥–ª—è –≤—Ö–æ–¥–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö JSON
interface ChallengeInput {
  id: string;
  title: string;
  description: string;
  category: string;
  difficulty: string;
  points: number;
  tags: string[];
  solved: boolean;
  solvedBy: number;
  createdAt: string;
  updatedAt: string;
  downloadUrl: string;
  fileType: string;
  hints: string[];
  flag: string;
}

export default function AddChallenge() {
  const navigate = useNavigate();

  const handleTestImport = () => {
    fetch('/test-challenge.json', { method: 'GET' })
      .then(response => {
        if (!response.ok) {
          throw new Error(`–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —Ñ–∞–π–ª (—Å—Ç–∞—Ç—É—Å: ${response.status})`);
        }
        alert('–§–∞–π–ª —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω, –ø–∞—Ä—Å–∏–º JSON...');
        return response.json();
      })
      .then((json: ChallengeInput[]) => {
        alert(`–ü–æ–ª—É—á–µ–Ω JSON: ${JSON.stringify(json, null, 2)}`);
        if (!Array.isArray(json)) {
          throw new Error('JSON –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –º–∞—Å—Å–∏–≤–æ–º');
        }

        const newChallenges: Challenge[] = json.map((item: ChallengeInput) => {
          alert(`–ü—Ä–æ–≤–µ—Ä—è–µ–º –∫–µ–π—Å: ${JSON.stringify(item)}`);
          if (!item.id || !item.title || !item.description || !item.category || !item.difficulty || !item.points || !item.tags || !item.solvedBy) {
            throw new Error('–ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –∫–µ–π—Å–∞: –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è');
          }

          if (!item.hints || !Array.isArray(item.hints) || item.hints.length === 0) {
            throw new Error(`–ö–µ–π—Å —Å ID ${item.id} –¥–æ–ª–∂–µ–Ω —Å–æ–¥–µ—Ä–∂–∞—Ç—å –º–∞—Å—Å–∏–≤ –ø–æ–¥—Å–∫–∞–∑–æ–∫ (hints)`);
          }
          if (!item.flag || typeof item.flag !== 'string') {
            throw new Error(`–ö–µ–π—Å —Å ID ${item.id} –¥–æ–ª–∂–µ–Ω —Å–æ–¥–µ—Ä–∂–∞—Ç—å —Ñ–ª–∞–≥ (flag) –≤ –≤–∏–¥–µ —Å—Ç—Ä–æ–∫–∏`);
          }

          return {
            id: item.id,
            title: item.title,
            description: item.description,
            category: item.category,
            difficulty: item.difficulty,
            points: item.points,
            tags: item.tags,
            solved: item.solved !== undefined ? item.solved : false,
            solvedBy: item.solvedBy,
            createdAt: item.createdAt ? new Date(item.createdAt) : new Date(),
            updatedAt: item.updatedAt ? new Date(item.updatedAt) : new Date(),
            downloadUrl: item.downloadUrl || '',
            fileType: item.fileType || '',
          };
        });

        const existingIds = new Set(mockChallenges.map(c => c.id));
        for (const challenge of newChallenges) {
