  id: string;
  name: string;
  description: string;
  icon: string;
  earnedAt: Date;
}

// CTF Challenge types
export interface Challenge {
  id: string;
  title: string;
  description: string;
  category: ChallengeCategory;
  difficulty: 'beginner' | 'intermediate' | 'advanced' | 'expert';
  points: number;
  tags: string[];
