  features: string[];
  isFeatured: boolean;
  isPopular: boolean;
  comingSoon: boolean;
  category: 'scanner' | 'monitor' | 'crypto' | 'pentest' | 'devsec' | 'network' | 'other';
}

// Community types
export interface Event {
  id: string;
  title: string;
  description: string;
  date: Date;
  timeZone: string;
  type: 'workshop' | 'competition' | 'webinar' | 'meetup';
