import { NavigateFunction } from 'react-router-dom';
import { Dispatch, SetStateAction } from 'react';
import { User } from '@/types';
import { apiClient } from '@/lib/api';
import { toast } from '@/components/ui/use-toast';

interface AuthResponse {
  user: {
    id: number;
    username: string;
    email: string;
    avatar?: string;
    role: string;
    points: number;
    level: number;
    emailVerified?: boolean;
  };
  token: string;
  message?: string;
}

interface RegisterResponse {
  userId: number;
  email: string;
  message: string;
  verificationRequired?: boolean;
  developmentCode?: string;
}

export const login = async (
  email: string,
  password: string,
  setIsLoading: Dispatch<SetStateAction<boolean>>,
  setError: Dispatch<SetStateAction<string | null>>
): Promise<AuthResponse | null> => {
  setIsLoading(true);
  setError(null);

  try {
    const response = await fetch('/api/auth/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    });

    const data = await response.json();

    if (!response.ok) {
      setError(data.message || 'Login failed');
      toast({
        title: "–û—à–∏–±–∫–∞ –≤—Ö–æ–¥–∞",
        description: data.message || '–ù–µ–≤–µ—Ä–Ω—ã–π email –∏–ª–∏ –ø–∞—Ä–æ–ª—å',
        variant: "destructive",
      });
      return null;
    }

    if (data.token) {
      apiClient.setToken(data.token);
    }

    toast({
      title: "–£—Å–ø–µ—à–Ω—ã–π –≤—Ö–æ–¥",
      description: "–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ CyberWhale!",
    });

    return data;
  } catch (err: any) {
    console.error('Unexpected login error:', err);
    setError(err.message);
    toast({
      title: "–û—à–∏–±–∫–∞ –≤—Ö–æ–¥–∞",
      description: "–ü—Ä–æ–∏–∑–æ—à–ª–∞ –Ω–µ–æ–∂–∏–¥–∞–Ω–Ω–∞—è –æ—à–∏–±–∫–∞ –ø—Ä–∏ –≤—Ö–æ–¥–µ",
      variant: "destructive",
    });
    return null;
  } finally {
    setIsLoading(false);
  }
};

export const register = async (
  username: string,
  email: string,
  password: string,
  setIsLoading: Dispatch<SetStateAction<boolean>>,
  setError: Dispatch<SetStateAction<string | null>>,
  navigate: NavigateFunction
): Promise<RegisterResponse | null> => {
  setIsLoading(true);
  setError(null);

  try {
    const response = await fetch('/api/auth/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, email, password }),
    });

    const data = await response.json();

    if (!response.ok) {
      setError(data.message || 'Registration failed');
      toast({
        title: "–û—à–∏–±–∫–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏",
        description: data.message,
        variant: "destructive",
      });
      return null;
    }

    if (data.verificationRequired) {
      toast({
        title: "–†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è –ø—Ä–æ—à–ª–∞ —É—Å–ø–µ—à–Ω–æ!",
        description: data.message,
      });

      if (data.developmentCode) {
        console.log('Verification code:', data.developmentCode);
      }

      navigate('/auth/verify-otp', { state: { email: data.email } });
    }

    return data;
  } catch (err: any) {
    console.error('Unexpected registration error:', err);
    setError(err.message);
    toast({
      title: "–û—à–∏–±–∫–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏",
      description: "–ü—Ä–æ–∏–∑–æ—à–ª–∞ –Ω–µ–æ–∂–∏–¥–∞–Ω–Ω–∞—è –æ—à–∏–±–∫–∞ –ø—Ä–∏ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏",
      variant: "destructive",
    });
    return null;
  } finally {
    setIsLoading(false);
  }
};

export const logout = async (
  setUser: Dispatch<SetStateAction<User | null>>,
  setIsLoading: Dispatch<SetStateAction<boolean>>,
  setError: Dispatch<SetStateAction<string | null>>,
  navigate: NavigateFunction
): Promise<void> => {
  setIsLoading(true);

  try {
    apiClient.clearToken();
    setUser(null);
    navigate('/');

    toast({
      title: "–í—ã—Ö–æ–¥ –≤—ã–ø–æ–ª–Ω–µ–Ω",
      description: "–í—ã —É—Å–ø–µ—à–Ω–æ –≤—ã—à–ª–∏ –∏–∑ —Å–∏—Å—Ç–µ–º—ã",
