// Simple email simulation service for development
// In production, this would send real emails

interface EmailData {
  to: string;
  subject: string;
  text: string;
  html?: string;
}

export class EmailService {
  private static instance: EmailService;
  private verificationCodes: Map<string, { code: string; expires: Date }> = new Map();

  public static getInstance(): EmailService {
    if (!EmailService.instance) {
      EmailService.instance = new EmailService();
    }
    return EmailService.instance;
  }

  // Generate 6-digit verification code
  generateVerificationCode(): string {
    return Math.floor(100000 + Math.random() * 900000).toString();
  }

  // Simulate sending email by logging to console
  async sendEmail(emailData: EmailData): Promise<boolean> {
    try {
      console.log('\n=== EMAIL –û–¢–ü–†–ê–í–õ–ï–ù ===');
      console.log(`–ö–æ–º—É: ${emailData.to}`);
      console.log(`–¢–µ–º–∞: ${emailData.subject}`);
      console.log(`–°–æ–æ–±—â–µ–Ω–∏–µ: ${emailData.text}`);
      console.log('=====================\n');

      // In development, we'll also show this in the response
      return true;
    } catch (error) {
      console.error('Email sending error:', error);
      return false;
    }
  }

  async sendVerificationEmail(email: string, code: string): Promise<boolean> {
    const emailData: EmailData = {
      to: email,
      subject: 'CyberWhale - –ü–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏',
      text: `–í–∞—à –∫–æ–¥ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è: ${code}\n\n–í–≤–µ–¥–∏—Ç–µ —ç—Ç–æ—Ç –∫–æ–¥ –Ω–∞ —Å–∞–π—Ç–µ –¥–ª—è –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏.\n\n–ö–æ–¥ –¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª–µ–Ω 15 –º–∏–Ω—É—Ç.`,
      html: `
        <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
          <h2 style="color: #1a365d;">CyberWhale - –ü–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏</h2>
          <p>–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ CyberWhale!</p>
          <div style="background: #f7fafc; padding: 20px; border-radius: 8px; text-align: center; margin: 20px 0;">
            <h3 style="color: #2d3748;">–í–∞—à –∫–æ–¥ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è:</h3>
            <div style="font-size: 32px; font-weight: bold; color: #3182ce; letter-spacing: 8px;">${code}</div>
          </div>
          <p>–í–≤–µ–¥–∏—Ç–µ —ç—Ç–æ—Ç –∫–æ–¥ –Ω–∞ —Å–∞–π—Ç–µ –¥–ª—è –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏.</p>
          <p style="color: #718096; font-size: 14px;">–ö–æ–¥ –¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª–µ–Ω 15 –º–∏–Ω—É—Ç.</p>
        </div>
      `
    };

    return await this.sendEmail(emailData);
  }

  async sendPasswordResetEmail(email: string, code: string): Promise<boolean> {
    const emailData: EmailData = {
      to: email,
      subject: 'CyberWhale - –í–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏–µ –ø–∞—Ä–æ–ª—è',
      text: `–ö–æ–¥ –¥–ª—è –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏—è –ø–∞—Ä–æ–ª—è: ${code}\n\n–í–≤–µ–¥–∏—Ç–µ —ç—Ç–æ—Ç –∫–æ–¥ –Ω–∞ —Å–∞–π—Ç–µ –¥–ª—è —Å–±—Ä–æ—Å–∞ –ø–∞—Ä–æ–ª—è.\n\n–ö–æ–¥ –¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª–µ–Ω 15 –º–∏–Ω—É—Ç.`,
      html: `
        <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto;">
          <h2 style="color: #1a365d;">CyberWhale - –í–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏–µ –ø–∞—Ä–æ–ª—è</h2>
          <p>–í—ã –∑–∞–ø—Ä–æ—Å–∏–ª–∏ –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏–µ –ø–∞—Ä–æ–ª—è.</p>
          <div style="background: #f7fafc; padding: 20px; border-radius: 8px; text-align: center; margin: 20px 0;">
            <h3 style="color: #2d3748;">–ö–æ–¥ –¥–ª—è –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏—è:</h3>
            <div style="font-size: 32px; font-weight: bold; color: #e53e3e; letter-spacing: 8px;">${code}</div>
          </div>
          <p>–í–≤–µ–¥–∏—Ç–µ —ç—Ç–æ—Ç –∫–æ–¥ –Ω–∞ —Å–∞–π—Ç–µ –¥–ª—è —Å–±—Ä–æ—Å–∞ –ø–∞—Ä–æ–ª—è.</p>
          <p style="color: #718096; font-size: 14px;">–ö–æ–¥ –¥–µ–π—Å—Ç–≤–∏—Ç–µ–ª–µ–Ω 15 –º–∏–Ω—É—Ç.</p>
        </div>
      `
    };

    return await this.sendEmail(emailData);
  }

  // Store verification code temporarily (in production, use Redis or similar)
  storeVerificationCode(email: string, code: string): void {
    const expires = new Date();
    expires.setMinutes(expires.getMinutes() + 15); // 15 minutes expiry
    this.verificationCodes.set(email, { code, expires });
  }

  // Verify code
  verifyCode(email: string, inputCode: string): boolean {
    const stored = this.verificationCodes.get(email);
    if (!stored) return false;

    if (new Date() > stored.expires) {
      this.verificationCodes.delete(email);
      return false;
    }

    if (stored.code === inputCode) {
      this.verificationCodes.delete(email);
      return true;
    }

    return false;
  }
}

export const emailService = EmailService.getInstance();
